import React,{useState} from 'react';
import "../styles/Orderdetails.css";


function Orderdetails(props){
  const {itemCount,totalPrice,cartItems}=props;
  const [fullDetailFlag,setfullDetailFlag]=useState(false);

  function handleToggleDetails(){
      setfullDetailFlag(!fullDetailFlag);
  }


  return (
    <div>
        <div className="toggleFullCart">
          {fullDetailFlag ?
              cartItems.map(item => {
                  return(<div >
                          <h1>{item.itemName}</h1>
                          <h3>{item.itemPrice}</h3>
                        </div>
                      );
              })
              : null
          }
        </div>

        <div className="order-details">
          <button className="btn btn-lgdetail-order-btn" onClick={handleToggleDetails}>Full details</button>
          <h4 className="item-count">Your Order ({itemCount})</h4>
          <h4 className="total-price">Subtotal: â‚¹{totalPrice} </h4>
          <button className="btn btn-lg continuebtn">Continue</button>
        </div>


    </div>
  )
}

export default Orderdetails;
