    return (
        <div>
            <img src={props.image} alt="restaurant" className="res-img" />
            <h4 className="name">{props.name}</h4>
            <p className="rating">{props.rating}‚≠ê</p>
            <p>{props.address}</p>
        </div>
    );
}

export default Restaurant;
