import {createSlice,configureStore} from "@reduxjs/toolkit";

const initialCartState={totalPrice:0,totalItemCount:0,cartItems:[]};

const cartDetails = createSlice({
  name:'totalPriceSlice',
  inititalState:initialCartState,
  reducers : {
    addItem(state={initialCartState},action){
        if(!state.cartItems.includes(action.payload.item)){
          state.cartItems.push(action.payload.item);
        }
        state.totalPrice=state.totalPrice+action.payload.item.itemPrice;
        state.totalItemCount=state.totalItemCount+1;
    },
    removeItem(state={initialCartState},action){
      const currItem = state.cartItems.find(item=>item===action.payload.item);
      if(currItem.itemCount===1){
        state.cartItems=state.cartItems.find(item=>item!==action.payload.item);
      }
      state.totalPrice=state.totalPrice-currItem.itemPrice;
      state.totalItemCount>=1?state.totalItemCount=state.totalItemCount-1:state.totalItemCount=0;
    },
  }
});

const store = configureStore({
  reducer:{cartDetails:cartDetails.reducer}
});

export const cartActions = cartDetails.reducer;

export default store;
